<?php

namespace Coretik\PageBuilder\Blocks\Block\Tools;

use StoutLogic\AcfBuilder\FieldsBuilder;
use Coretik\PageBuilder\Blocks\Block;

class Anchor extends Block
{
    const NAME = 'tools.anchor';
    const LABEL = 'Outils: Ancre';
    const SCREENSHOTABLE = false;

    protected $anchor;

    public function thumbnail(): string
    {
        return "data:image/jpeg;base64,";
    }

    public function fieldsBuilder($fieldName = 'content'): FieldsBuilder
    {
        $field = new FieldsBuilder($this->getName(), $this->fieldsBuilderConfig());
        $field->addText('anchor')
                ->setLabel(__('Ancre', app()->get('settings')['text-domain']))
                ->setInstructions(__('Permet de cibler une partie de la page via une url se terminant par "#ancre"', app()->get('settings')['text-domain']));

        // Sample How to use settings
        // Use trait Settings/FlowSettings
        $this->useSettingsOn($field);

        return $field;
    }

    public function toArray()
    {
        return [
            'anchor' => $this->anchor,
        ];
    }
}
